/********************************
	-	FLAT LIGHT BUTTONS -
*********************************/
.flat-light .navigationbuttons,
.flat-light .esg-pagination,
.flat-light .esg-filters{	text-transform:uppercase;
							text-align: center;
						}

.flat-light .esg-filterbutton,
.flat-light .esg-navigationbutton,
.flat-light .esg-sortbutton,
.flat-light .esg-cartbutton {	color:#000;
								margin-right:5px;
								cursor:pointer;
								position: relative;
								z-index:2;
								padding:1px 30px;
								border:none;
								line-height:38px;
								border-radius:5px;
								-moz-border-radius:5px;
								-webkit-border-radius:5px;
								font-size:12px;
								font-weight:700;
								font-family:"Open Sans",sans-serif;
								display: inline-block;
								background: #fff;
								margin-bottom:5px;
							}

.flat-light .esg-navigationbutton	{ padding:2px 12px; }
.flat-light .esg-navigationbutton *	{ color:#000; }
.flat-light .esg-pagination-button:last-child { margin-right: 0; }

.flat-light  .esg-sortbutton-wrapper,
.flat-light  .esg-cartbutton-wrapper { display:inline-block; }
.flat-light  .esg-sortbutton-order,
.flat-light  .esg-cartbutton-order {	display:inline-block;
										vertical-align:top;
										border:none;
										width:40px;
										line-height:40px;
										border-radius:5px;
										-moz-border-radius:5px;
										-webkit-border-radius:5px;
										font-size:12px;
										font-weight:700;
										color:#999;
										cursor: pointer;
										background:#eee;
										background: #fff;
										margin-left:5px;
									}

.flat-light .esg-cartbutton {	color:#fff;
								cursor: default !important;
							}
.flat-light .esg-cartbutton .esgicon-basket	{
												color:#fff;
												font-size:15px;
												line-height:15px;
												margin-right:10px;
											}
.flat-light .esg-cartbutton-wrapper { cursor: default !important; }

.flat-light .esg-sortbutton,
.flat-light .esg-cartbutton {	display:inline-block;
								position:relative;
								cursor: pointer;
								margin-right:0px;
								border-radius:5px;
								-moz-border-radius:5px;
								-webkit-border-radius:5px;
							}

.flat-light .esg-navigationbutton:hover,
.flat-light .esg-filterbutton:hover,
.flat-light .esg-sortbutton:hover,
.flat-light .esg-sortbutton-order:hover,
.flat-light .esg-cartbutton-order:hover,
.flat-light .esg-filterbutton.selected {
										border-color:none;color:#000;
										background:#fff;
									   }

.flat-light .esg-navigationbutton:hover * { color:#333; }

.flat-light .esg-sortbutton-order.tp-desc:hover	{ color:#333; }

.flat-light .esg-filter-checked	{	padding:1px 3px;
									color:#cbcbcb;
									background:#cbcbcb;
									margin-left:7px;
									font-size:9px;
									font-weight:300;
									line-height:9px;
									vertical-align: middle;
								}
.flat-light .esg-filterbutton.selected .esg-filter-checked,
.flat-light .esg-filterbutton:hover .esg-filter-checked	{
															padding:1px 3px 1px 3px;
															color:#fff;
															background:#000;
															margin-left:7px;
															font-size:9px;
															font-weight:300;
															line-height:9px;
															vertical-align: middle;
														}


/* FLAT LIGHT SKIN DROP DOWN 1.1.0 */
.flat-light .esg-filterbutton 								{ 	color:#000;color:rgba(0,0,0,0.5);}

.flat-light	.esg-selected-filterbutton						{	background:#fff; padding:10px 20px 10px 30px; color:#000; border-radius: 4px;font-weight:700;}

.flat-light .esg-cartbutton,
.flat-light .esg-cartbutton a,
.flat-light .esg-cartbutton a:visited,
.flat-light .esg-cartbutton a:hover,
.flat-light .esg-cartbutton i,
.flat-light .esg-cartbutton i.before								{	font-weight:700; color:#000; }
.flat-light .esg-selected-filterbutton .eg-icon-down-open	{	 margin-left:5px;font-size:12px; line-height: 20px; vertical-align: top;}

.flat-light .esg-selected-filterbutton:hover .eg-icon-down-open,
.flat-light .esg-selected-filterbutton.hoveredfilter .eg-icon-down-open	{	 color:rgba(0,0,0,1); }

.flat-light .esg-dropdown-wrapper							{	border-radius:5px;-moz-border-radius:5px;-webkit-border-radius:5px;}
.flat-light .esg-dropdown-wrapper .esg-filterbutton			{	line-height: 25px; white-space: nowrap; padding:0px 10px; font-weight:700; text-align: left}
.flat-light .esg-dropdown-wrapper .esg-filter-checked		{	display:inline-block; margin-left:0px !important;margin-right:7px; margin-top:-2px !important; line-height: 15px !important;}
.flat-light .esg-dropdown-wrapper .esg-filter-checked span	{	vertical-align: middle; line-height:20px;}

